package programs1;import java.util.function.Function; public class AndThenFunction {     public static void main(String[] args) {        Function<Integer, Integer> f1 = i -> i*i;        Function<Integer, Integer> f2 = i -> i*2;        System.out.println("Square the number 4 and then double it :"+f1.andThen(f2).apply(4));        System.out.println("Double the number 4 and then Square it :"+f1.compose(f2).apply(4));    }    }